declare module '*.svg' {
  import React from 'react';
  export const ReactComponent: React.FunctionComponent<React.SVGProps<SVGSVGElement>>;
  const src: string;
  export default src;
}

declare module '*.png' {
  const src: string;
  export default src;
}

declare module '*.jpg' {
  const src: string;
  export default src;
}

declare module '*.jpeg' {
  const src: string;
  export default src;
}

declare module '*.gif' {
  const src: string;
  export default src;
}

declare module '*.webp' {
  const src: string;
  export default src;
}

declare module '*.css' {
  const classes: { readonly [key: string]: string };
  export default classes;
}

declare module '*.module.css' {
  const classes: { readonly [key: string]: string };
  export default classes;
}

// Cardano Wallet Extension Types
interface CardanoWallet {
  name?: string;
  icon?: string;
  apiVersion?: string;
  enable?: () => Promise<any>;
  isEnabled?: () => Promise<boolean>;
}

declare global {
  interface Window {
    cardano?: {
      [key: string]: CardanoWallet;
    };
  }
}
